create table house
(
    house_id     bigint(200) auto_increment
        primary key,
    address      varchar(1000)  not null,
    des          varchar(10000) null,
    house_name   varchar(1000)  not null,
    numBathrooms int            not null,
    numBedrooms  int            not null,
    price        float          not null,
    type_id      bigint         null,
    sourcePath   varchar(255)   not null,
    owner_id     bigint         null
)
    engine = MyISAM;

create index FK4r0nxoot9tsovfbhhopg2mhi
    on house (owner_id);

create index FKoyh55b0ttt5x0cd0odrd2kwek
    on house (type_id);

